import rp from 'request-promise-native';

const { FB_MESSENGER_PAGE_ACCESS_TOKEN } = process.env;

/*
 * Call the Send API. The message data goes in the body. If successful, we'll
 * get the message id in a response
 *
 */
function callSendAPI(messageData) {
  rp({
    uri: 'https://graph.facebook.com/v2.6/me/messages',
    qs: { access_token: FB_MESSENGER_PAGE_ACCESS_TOKEN },
    method: 'POST',
    json: messageData
  })
    .then(response => {
      const messageId = response['message_id'];
      const recipientId = response['recipient_id'];
      // if (messageId) {
      //   console.log(`Successfully sent message with id ${messageId} to recipient ${recipientId}`);
      // }
      // else {
      //   console.log(`Successfully called Send API for recipient ${recipientId}`);
      // }
    })
    .catch(err => console.error(err));
}

/*
 * Send a text message using the Send API.
 *
 */
export default function sendTextMessage(recipientId, messageText) {
  var messageData = {
    recipient: {
      id: recipientId
    },
    message: {
      text: messageText,
      metadata: 'DEVELOPER_DEFINED_METADATA'
    }
  };

  callSendAPI(messageData);
}

